import React from "react";

const Register = ({ showRegisterForm, onRegister }) => {
  if (!showRegisterForm) {
    // Ако не искате да показвате формата за регистрация, върнете null
    return null;
  }

  return (
    <div>
      <h2>Регистрация</h2>
      <form>
        <label>
          Потребителско име:
          <input type="text" name="username" />
        </label>
        <br />
        <label>
          Парола:
          <input type="password" name="password" />
        </label>
        <br />
        <label>
          Имейл:
          <input type="email" name="email" />
        </label>
        <br />
        {/* Когато потребителят натисне бутона за регистрация, извикваме onRegister */}
        <button type="button" onClick={onRegister}>
          Регистрация
        </button>
      </form>
    </div>
  );
};

export default Register;
