import { useForm } from "../../hook/useForm";
import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../contexts/AuthContext";

const LoginFormKeys = {
    Email: 'email',
    Password: 'password',
}

// Дефиниране на userLogin
const userLogin = {
    username: 'admin@abv.bg',
    password: 'admin'
}

export const Login = () => {
    const { onLoginSubmit } = useContext(AuthContext);
    const { values, changeHandler, onSubmit } = useForm({
        [LoginFormKeys.Email]: '',
        [LoginFormKeys.Password]: '',
    }, onLoginSubmit)

    const handleLogin = async () => {
        // Проверка на потребителско име и парола, използвайки userLogin
        if (values[LoginFormKeys.Email] === userLogin.username && values[LoginFormKeys.Password] === userLogin.password) {
            // Успешен вход
            await onLoginSubmit(values); // Изпращаме данните на функцията за вход
        } else {
            // Неуспешен вход - може да изведете съобщение за грешка
            console.log("Невалидно потребителско име или парола");
        }
    }

    return (
        <div className="auth-form-container">
            <h2>Login</h2>
            <form className="login-form" method="POST" onSubmit={onSubmit}>
                <label htmlFor="email">email</label>
                <input
                    value={values[LoginFormKeys.Email]}
                    onChange={changeHandler}
                    type="email"
                    placeholder="example@gmail.com"
                    id="email"
                    name={LoginFormKeys.Email}
                />
                <label htmlFor="password">password</label>
                <input
                    value={values[LoginFormKeys.Password]}
                    onChange={changeHandler}
                    type="password"
                    placeholder="********"
                    id="password"
                    name={LoginFormKeys.Password}
                />
                <button type="button" onClick={handleLogin}>Log In</button> {/* Използваме onClick за обработка на вход */}
            </form>
            <button className="link-btn">
                <Link to="/register">Don't have an account? Register here.</Link>
            </button>
        </div>
    );
};
