import { Fragment, useState } from 'react';

import HeaderCartButton from './HeaderCartButton';
import mealsImage from '../../assets/meals.jpg';
import classes from './Header.module.css';
import { Link } from 'react-router-dom';

const Header = (props) => {
  // Създаваме състояние, което ще отразява дали потребителят е логнат
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Функция за логин
  const loginHandler = () => {
    // Симулираме логин логиката
    const username = prompt('Моля, въведете потребителско име:');
    const password = prompt('Моля, въведете парола:');

    // Проверяваме дали потребителското име и паролата са правилни
    if (username === 'потребител' && password === 'парола') {
      // Ако са правилни, актуализираме състоянието за логин
      setIsLoggedIn(true);
    } else {
      // Ако не са правилни, може да изведете съобщение за грешка или да направите друго нещо
      alert('Грешно потребителско име или парола. Моля, опитайте отново.');
    }
  };

  return (
    <Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        {isLoggedIn ? (
          <p>Добре дошъл, Потребителско Име</p>
        ) : (
          <button onClick={loginHandler}>Логин</button>
        )}
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes['main-image']}>
        <img src={mealsImage} alt='A table full of delicious food!' />
      </div>
    </Fragment>
  );
};

export default Header;
