import React, { useState } from "react";
import axios from "axios";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleRegister = async () => {
    try {
      // Изпращаме данните на сървъра
      await axios.post("http://localhost:3030/register", formData);

      // Успешна регистрация - извършваме автоматичен логин на клиента
      const loginResponse = await axios.post("http://localhost:3030/login", {
        username: formData.username,
        password: formData.password,
      });

      // Тук можете да обработите отговора от сървъра след логина
      // Например, актуализирайте състоянието на приложението, за да покажете, че потребителят е влязъл в системата
    } catch (error) {
      // Обработете грешката, ако регистрацията не бъде успешна
    }
  };

  return (
    <div>
      <h2>Регистрация</h2>
      <form>
        <label>
          Потребителско име:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Парола:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Имейл:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="button" onClick={handleRegister}>
          Регистрация
        </button>
      </form>
    </div>
  );
};

export default Register;
